#include "core/crColor.h"Color::Color(){}Color::Color(CRfloat p_red, CRfloat p_green, CRfloat p_blue, CRfloat p_alpha){	_red = p_red;	_green = p_green;	_blue = p_blue;	_alpha = p_alpha;}Color::~Color(){}void Color::setValues(CRfloat p_red, CRfloat p_green, CRfloat p_blue, CRfloat p_alpha){    _red = p_red;	_green = p_green;	_blue = p_blue;	_alpha = p_alpha;}void Color::getColor16f(CRfloat* p_vertexColor){	//float *vertexColor = ( float * ) calloc( 1, 16  * sizeof( float ));    p_vertexColor = ( float * ) calloc( 1, 16  * sizeof( float ));    	// Get the color coordinates	p_vertexColor[0] = _red;	p_vertexColor[1] = _green;	p_vertexColor[2] = _blue;	p_vertexColor[3] = _alpha;	//	p_vertexColor[4] = _red;	p_vertexColor[5] = _green;	p_vertexColor[6] = _blue;	p_vertexColor[7] = _alpha;	//	p_vertexColor[8] = _red;	p_vertexColor[9] = _green;	p_vertexColor[10] = _blue;	p_vertexColor[11] = _alpha;	//	p_vertexColor[12] = _red;	p_vertexColor[13] = _green;	p_vertexColor[14] = _blue;	p_vertexColor[15] = _alpha;}